4. Create API hooks for users in `lib/hooks/use-users.ts`:
```typescript
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { fetchApi } from "@/lib/api-utils";
import type { User } from "@prisma/client";
import type { ApiResponse } from "@/lib/types";

// Get all users
export function useUsers(page = 1, pageSize = 10) {
  return useQuery<ApiResponse<User[]>>({
    queryKey: ["users", { page, pageSize }],
    queryFn: () => fetchApi<User[]>(`/api/users?page=${page}&pageSize=${pageSize}`),
  });
}

// Get user by ID
export function useUser(id: string) {
  return useQuery<User>({
    queryKey: ["users", id],
    queryFn: () => fetchApi<User>(`/api/users/${id}`).then(res => res.data),
    enabled: !!id,
  });
}

// Create user
export function useCreateUser() {
  const queryClient = useQueryClient();

  return useMutation<User, Error, Omit<User, "id" | "createdAt" | "updatedAt">>(
    (newUser) => fetchApi<User>("/api/users", {
      method: "POST",
      body: JSON.stringify(newUser),
    }).then(res => res.data),
    {
      onSuccess: () => {
        queryClient.invalidateQueries({ queryKey: ["users"] });
      },
    }
  );
}

// Update user
export function useUpdateUser() {
  const queryClient = useQueryClient();

  return useMutation<User, Error, { id: string; data: Partial<User> }>(
    ({ id, data }) => fetchApi<User>(`/api/users/${id}`, {
      method: "PUT",
      body: JSON.stringify(data),
    }).then(res => res.data),
    {
      onSuccess: (data) => {
        queryClient.invalidateQueries({ queryKey: ["users", data.id] });
        queryClient.invalidateQueries({ queryKey: ["users"] });
      },
    }
  );
}

// Delete user
export function useDeleteUser() {
  const queryClient = useQueryClient();

  return useMutation<void, Error, string>(
    (id) => fetchApi<void>(`/api/users/${id}`, {
      method: "DELETE",
    }).then(() => undefined),
    {
      onSuccess: () => {
        queryClient.invalidateQueries({ queryKey: ["users"] });
      },
    }
  );
}
```

